mapper.select:
select abb.BUILDING_ID, abb.NAME BUILDING_NAME, arr.ROOM_ID, arr.HOUSE_NUMBER, mr.WATER_READING,mr.ELECTRICITY_READING,mr.READING_DATE from archi_r_room arr join archi_b_building abb on arr.BUILDING_ID = abb.BUILDING_ID left join meter_reading mr on arr.ROOM_ID = mr.ROOM_ID
and year(mr.READING_DATE)=#{year} and month(mr.READING_DATE)=#{month} and day(mr.READING_DATE)=#{day}
where arr.BUILDING_ID = #{buildingId}

mapper.sql:
select r1.ID,r1.ROOM_ID,r1.WATER_READING,r1.ELECTRICITY_READING,r1.READING_DATE, r2.HOUSE_NUMBER, r3.BUILDING_ID,r3.NAME BUILDING_NAME from meter_reading r1 JOIN archi_r_room r2 on r1.ROOM_ID = r2.ROOM_ID
and r2.HOUSE_NUMBER = #{houseNumber}
JOIN archi_b_building r3 on r2.BUILDING_ID = r3.BUILDING_ID
and r3.NAME = #{buildingName}
WHERE 1=1
and YEAR(READING_DATE) = #{year} and MONTH(READING_DATE) = #{month}
